-- ----------------------------
-- Table structure for author_follow
-- ----------------------------
DROP TABLE IF EXISTS `author_follow`;
CREATE TABLE `author_follow`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '关注ID',
  `follower_id` bigint NOT NULL COMMENT '关注者ID',
  `author_id` bigint NOT NULL COMMENT '被关注的作者ID',
  `is_delete` int NOT NULL DEFAULT 0 COMMENT '是否关注：0-关注  1-取消关注',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '关注时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `idx_follower_author`(`follower_id`, `author_id`) USING BTREE,
  INDEX `idx_author_id`(`author_id`) USING BTREE,
  INDEX `idx_follower_id`(`follower_id`) USING BTREE,
  CONSTRAINT `fk_follow_author` FOREIGN KEY (`author_id`) REFERENCES `user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_follow_follower` FOREIGN KEY (`follower_id`) REFERENCES `user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '作者关注关系表' ROW_FORMAT = DYNAMIC;

