-- ----------------------------
-- Table structure for author_profile
-- ----------------------------
DROP TABLE IF EXISTS `author_profile`;
CREATE TABLE `author_profile`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '档案ID',
  `author_id` bigint NOT NULL COMMENT '用户ID（关联user表）',
  `pen_name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '笔名',
  `bio` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '作者简介',
  `specialties` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '擅长领域（多个用逗号分隔）',
  `works_count` int NOT NULL DEFAULT 0 COMMENT '作品数量',
  `total_words` bigint NOT NULL DEFAULT 0 COMMENT '总创作字数',
  `fans_count` int NOT NULL DEFAULT 0 COMMENT '粉丝数量',
  `following_count` int NOT NULL DEFAULT 0 COMMENT '关注数量',
  `total_reads` bigint NOT NULL DEFAULT 0 COMMENT '总阅读量',
  `total_favorites` bigint NOT NULL DEFAULT 0 COMMENT '总收藏量',
  `author_level` tinyint NOT NULL DEFAULT 1 COMMENT '作者等级：1-新手，2-进阶，3-资深，4-大神，5-殿堂级',
  `certification_status` tinyint NOT NULL DEFAULT 0 COMMENT '认证状态：0-未认证，1-个人认证，2-机构认证',
  `certification_info` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '认证信息',
  `social_weibo` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '微博链接',
  `social_wechat` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '微信公众号',
  `social_qq` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT 'QQ号',
  `contact_email` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '联系邮箱',
  `homepage_url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '个人主页链接',
  `awards` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '获奖经历（JSON格式）',
  `monthly_income` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '月度收入',
  `is_contracted` tinyint NOT NULL DEFAULT 0 COMMENT '是否签约：0-未签约，1-已签约',
  `contract_level` tinyint NULL DEFAULT NULL COMMENT '签约等级：1-青铜，2-白银，3-黄金，4-铂金，5-钻石',
  `last_active_time` datetime NULL DEFAULT NULL COMMENT '最后活跃时间',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `idx_user_id`(`author_id`) USING BTREE,
  INDEX `idx_author_level`(`author_level`) USING BTREE,
  INDEX `idx_certification_status`(`certification_status`) USING BTREE,
  INDEX `idx_works_count`(`works_count`) USING BTREE,
  INDEX `idx_fans_count`(`fans_count`) USING BTREE,
  CONSTRAINT `fk_author_profile_user` FOREIGN KEY (`author_id`) REFERENCES `user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '作者档案扩展表' ROW_FORMAT = DYNAMIC;

