INSERT INTO `author_profile` VALUES (1, 2, '童话世界的编织者', '专注于儿童故事创作，用温暖的文字编织美好的童话世界，让每个孩子都能在故事中找到快乐和成长的力量。', '儿童文学,童话故事,教育故事', 5, 0, 1287, 0, 0, 0, 3, 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0.00, 0, NULL, NULL, '2025-06-24 11:53:56', '2025-06-24 19:33:48');
INSERT INTO `author_profile` VALUES (2, 3, '历史的述说者', '历史文学爱好者，擅长将枯燥的历史知识包装成生动有趣的故事，让读者在轻松阅读中了解传统文化。', '历史文学,传统文化,教育普及', 3, 0, 904, 0, 0, 0, 2, 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0.00, 0, NULL, NULL, '2025-06-24 11:53:56', '2025-06-24 20:10:57');

-- ----------------------------
-- Table structure for category
-- ----------------------------
DROP TABLE IF EXISTS `category`;
CREATE TABLE `category`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '分类ID',
  `name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '分类名称',
  `description` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '分类描述',
  `icon_url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '分类图标URL',
  `sort_order` int NOT NULL DEFAULT 0 COMMENT '排序权重',
  `parent_id` bigint NOT NULL DEFAULT 0 COMMENT '父分类ID，0表示一级分类',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `is_deleted` tinyint NOT NULL DEFAULT 0 COMMENT '是否删除：0-未删除，1-已删除',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_parent_id`(`parent_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 9 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '故事分类表' ROW_FORMAT = DYNAMIC;

