-- ----------------------------
-- Table structure for chapter
-- ----------------------------
DROP TABLE IF EXISTS `chapter`;
CREATE TABLE `chapter`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '章节ID',
  `story_id` bigint NOT NULL COMMENT '故事ID',
  `title` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '章节标题',
  `content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '章节内容',
  `chapter_index` int NOT NULL COMMENT '章节序号',
  `word_count` int NOT NULL DEFAULT 0 COMMENT '字数统计',
  `is_charged` tinyint NOT NULL DEFAULT 0 COMMENT '是否收费：0-免费，1-收费',
  `view_count` bigint NOT NULL DEFAULT 0 COMMENT '浏览次数',
  `comment_count` bigint NOT NULL DEFAULT 0 COMMENT '评论数量',
  `status` tinyint NOT NULL DEFAULT 1 COMMENT '章节状态：0-草稿，1-已发布',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `is_deleted` tinyint NOT NULL DEFAULT 0 COMMENT '是否删除：0-未删除，1-已删除',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_story_id`(`story_id`) USING BTREE,
  INDEX `idx_chapter_index`(`chapter_index`) USING BTREE,
  INDEX `idx_status`(`status`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 34 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '章节表' ROW_FORMAT = DYNAMIC;

