-- ----------------------------
-- Table structure for order
-- ----------------------------
DROP TABLE IF EXISTS `order`;
CREATE TABLE `order`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '订单ID',
  `order_no` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '订单编号',
  `user_id` bigint NOT NULL COMMENT '用户ID',
  `order_type` tinyint NOT NULL COMMENT '订单类型：1-章节购买，2-VIP订阅，3-充值',
  `related_id` bigint NOT NULL COMMENT '关联ID（章节ID或VIP套餐ID）',
  `amount` decimal(10, 2) NOT NULL COMMENT '订单金额',
  `payment_method` tinyint NULL DEFAULT NULL COMMENT '支付方式：1-微信，2-支付宝，3-余额',
  `status` tinyint NOT NULL DEFAULT 0 COMMENT '订单状态：0-未支付，1-已支付，2-已取消，3-已退款',
  `pay_time` datetime NULL DEFAULT NULL COMMENT '支付时间',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `idx_order_no`(`order_no`) USING BTREE,
  INDEX `idx_user_id`(`user_id`) USING BTREE,
  INDEX `idx_create_time`(`create_time`) USING BTREE,
  INDEX `idx_status`(`status`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '订单表' ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of order
-- ----------------------------

-- ----------------------------
-- Table structure for story
-- ----------------------------
DROP TABLE IF EXISTS `story`;
CREATE TABLE `story`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '故事ID',
  `title` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '故事标题',
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '故事简介',
  `author_id` bigint NOT NULL COMMENT '作者ID',
  `author_name` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '作者名称',
  `cover_url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '封面图URL',
  `category_id` bigint NOT NULL COMMENT '分类ID',
  `status` tinyint NOT NULL DEFAULT 0 COMMENT '故事状态：0-连载中，1-已完结',
  `word_count` bigint NOT NULL DEFAULT 0 COMMENT '字数统计',
  `chapter_count` int NOT NULL DEFAULT 0 COMMENT '总章节数',
  `read_count` bigint NOT NULL DEFAULT 0 COMMENT '阅读量',
  `favorite_count` bigint NOT NULL DEFAULT 0 COMMENT '收藏量',
  `comment_count` bigint NOT NULL DEFAULT 0 COMMENT '评论量',
  `score` float NOT NULL DEFAULT 0 COMMENT '评分（1-10分）',
  `is_charged` tinyint NOT NULL DEFAULT 0 COMMENT '是否收费：0-免费，1-收费',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `last_chapter_id` bigint NULL DEFAULT NULL COMMENT '最后更新章节ID',
  `last_chapter_name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '最后更新章节名称',
  `last_update_time` datetime NULL DEFAULT NULL COMMENT '最后更新时间',
  `is_deleted` tinyint NOT NULL DEFAULT 0 COMMENT '是否删除：0-未删除，1-已删除',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_author_id`(`author_id`) USING BTREE,
  INDEX `idx_category_id`(`category_id`) USING BTREE,
  INDEX `idx_status`(`status`) USING BTREE,
  INDEX `idx_create_time`(`create_time`) USING BTREE,
  INDEX `idx_last_update_time`(`last_update_time`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 11 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '故事表' ROW_FORMAT = DYNAMIC;

