DROP TABLE IF EXISTS `user_read_history`;
CREATE TABLE `user_read_history`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '历史记录ID',
  `user_id` bigint NOT NULL COMMENT '用户ID',
  `story_id` bigint NOT NULL COMMENT '故事ID',
  `chapter_id` bigint NOT NULL COMMENT '章节ID',
  `read_progress` int NOT NULL DEFAULT 0 COMMENT '阅读进度（字数位置）',
  `read_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '阅读时间',
  `is_deleted` tinyint NOT NULL DEFAULT 0 COMMENT '是否删除：0-未删除，1-已删除',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `idx_user_story_chapter`(`user_id`, `story_id`, `chapter_id`) USING BTREE,
  INDEX `idx_user_id`(`user_id`) USING BTREE,
  INDEX `idx_story_id`(`story_id`) USING BTREE,
  INDEX `idx_read_time`(`read_time`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 12 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '用户阅读历史表' ROW_FORMAT = DYNAMIC;

